import React, { useState } from 'react';
import axios from 'axios';

function ChangelogGenerator() {
    const [repoName, setRepoName] = useState('');
    const [days, setDays] = useState(7);
    const [commits, setCommits] = useState('');
    const [changelog, setChangelog] = useState('');
    const [version, setVersion] = useState('');  // New state for version

    const fetchCommits = async () => {
        try {
            const response = await axios.post('http://localhost:5000/fetch-commits', {
                repo: repoName,
                days: days
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            setCommits(response.data.commits.join('\n'));
        } catch (error) {
            console.error('Error fetching commits:', error.response?.data?.error || error.message);
        }
    };

    const generateChangelog = async () => {
        if (!commits.trim()) {
            alert('Please fetch or enter commits first');
            return;
        }

        try {
            const response = await axios.post(
                'http://localhost:5000/generate-changelog',
                { commits: commits },
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );

            if (response.data && response.data.changelog) {
                setChangelog(response.data.changelog);
            } else {
                throw new Error('Invalid response from server');
            }
        } catch (error) {
            console.error('Error details:', error.response || error);
            alert(`Failed to generate changelog: ${error.response?.data?.error || error.message}`);
        }
    };
    const saveChangelog = async () => {
        if (!version) {
            alert('Please enter a version number');
            return;
        }
        if (!changelog) {
            alert('Please generate a changelog first');
            return;
        }

        try {
            await axios.post('http://localhost:5000/save-changelog', {
                version: version,
                content: changelog
            });
            alert('Changelog saved successfully!');
            setVersion(''); // Reset version after successful save
        } catch (error) {
            console.error('Error saving changelog:', error.response?.data?.error || error.message);
        }
    };

    return (
        <div>
            <h1>Changelog Generator</h1>
            <div>
                <input
                    type="text"
                    value={repoName}
                    onChange={(e) => setRepoName(e.target.value)}
                    placeholder="owner/repo (e.g., facebook/react)"
                />
                <input
                    type="number"
                    value={days}
                    onChange={(e) => setDays(parseInt(e.target.value))}
                    placeholder="Number of days"
                />
                <button onClick={fetchCommits}>Fetch Commits</button>
            </div>
            <br />
            <textarea
                value={commits}
                onChange={(e) => setCommits(e.target.value)}
                placeholder="Fetched commits will appear here"
                rows={10}
                cols={50}
            />
            <br />
            <button onClick={generateChangelog}>Generate Changelog</button>
            <h2>Generated Changelog:</h2>
            <pre>{changelog}</pre>
            <div>
                <input
                    type="text"
                    value={version}
                    onChange={(e) => setVersion(e.target.value)}
                    placeholder="Enter version (e.g., 1.0.0)"
                />
                <button onClick={saveChangelog}>Save Changelog</button>
            </div>
        </div>
    );
}

export default ChangelogGenerator;
