import React, { useState } from 'react';
import axios from 'axios';
import './ChangelogGenerator.css';

function ChangelogGenerator() {
  const [repoName, setRepoName] = useState('');
  const [days, setDays] = useState(7);
  const [commits, setCommits] = useState('');
  const [changelog, setChangelog] = useState('');
  const [version, setVersion] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const fetchCommits = async () => {
    if (!repoName) {
      alert('Please enter a repository name');
      return;
    }
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/fetch-commits', {
        repo: repoName,
        days: days
      });
      setCommits(response.data.commits.join('\n'));
    } catch (error) {
      alert('Error fetching commits: ' + (error.response?.data?.error || error.message));
    }
    setIsLoading(false);
  };

  const generateChangelog = async () => {
    if (!commits.trim()) {
      alert('Please fetch or enter commits first');
      return;
    }
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/generate-changelog',
        { commits: commits }
      );
      setChangelog(response.data.changelog);
    } catch (error) {
      alert('Error generating changelog: ' + (error.response?.data?.error || error.message));
    }
    setIsLoading(false);
  };

  const saveChangelog = async () => {
    if (!version.trim()) {
      alert('Please enter a version number');
      return;
    }
    if (!changelog.trim()) {
      alert('Please generate a changelog first');
      return;
    }
    setIsLoading(true);
    try {
      await axios.post('http://localhost:5000/save-changelog', {
        version: version,
        content: changelog
      });
      alert('Changelog saved successfully!');
      setVersion('');
    } catch (error) {
      alert('Error saving changelog: ' + (error.response?.data?.error || error.message));
    }
    setIsLoading(false);
  };

  return (
    <div className="changelog-container">
      <div className="header">
        <h1>Changelog Generator</h1>
        <p>Generate and manage changelogs from your GitHub repository</p>
      </div>

      <div className="input-section">
        <div className="input-group">
          <input
            type="text"
            value={repoName}
            onChange={(e) => setRepoName(e.target.value)}
            placeholder="owner/repo (e.g., facebook/react)"
            className="text-input"
          />
          <input
            type="number"
            value={days}
            onChange={(e) => setDays(parseInt(e.target.value))}
            placeholder="Number of days"
            className="number-input"
          />
          <button
            onClick={fetchCommits}
            className="button primary"
            disabled={isLoading}
          >
            {isLoading ? 'Fetching...' : 'Fetch Commits'}
          </button>
        </div>

        <div className="textarea-container">
          <textarea
            value={commits}
            onChange={(e) => setCommits(e.target.value)}
            placeholder="Fetched commits will appear here"
            className="commits-textarea"
          />
        </div>

        <button
          onClick={generateChangelog}
          className="button secondary"
          disabled={isLoading}
        >
          {isLoading ? 'Generating...' : 'Generate Changelog'}
        </button>

        <div className="changelog-section">
          <h2>Generated Changelog:</h2>
          <pre className="changelog-preview">{changelog}</pre>
        </div>

        <div className="save-section">
          <input
            type="text"
            value={version}
            onChange={(e) => setVersion(e.target.value)}
            placeholder="Enter version (e.g., 1.0.0)"
            className="version-input"
          />
          <button
            onClick={saveChangelog}
            className="button primary"
            disabled={isLoading}
          >
            {isLoading ? 'Saving...' : 'Save Changelog'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChangelogGenerator;
